  
let menuButtonArea = document.getElementsByClassName("nav__item--top")[0],
cross = document.getElementsByClassName("nav__link--cross")[0],
bars = document.getElementsByClassName("nav__link--bars")[0];
menuButtonArea.addEventListener("click", function () {
let s = document.getElementsByClassName("nav__drop-down-menu")[0],
  e = document.getElementsByClassName("nav__drop-down-items")[0];
s.classList.toggle("display"),
  cross.classList.toggle("display"),
  bars.classList.toggle("no-display"),
  e.classList.toggle("display");

});

