let menuButtonArea=document.getElementsByClassName("nav__item--top")[0],cross=document.getElementsByClassName("nav__link--cross")[0],bars=document.getElementsByClassName("nav__link--bars")[0];menuButtonArea.addEventListener("click",(function(){let e=document.getElementsByClassName("nav__drop-down-menu")[0],t=document.getElementsByClassName("nav__drop-down-items")[0];e.classList.toggle("display"),cross.classList.toggle("display"),bars.classList.toggle("no-display"),t.classList.toggle("display")}));let sections=document.querySelectorAll(".appearSection"),scrollPos=window.scrollY;window.addEventListener("scroll",(function(){let e=window.scrollY,t=e>scrollPos;for(let e=0;e<sections.length;e++){let s=sections[e].getBoundingClientRect();window.innerHeight>s.top&&t&&s.top>0||window.innerHeight>s.top?(sections[e].style.animationName="appear",sections[e].style.animationPlayState="running"):sections[e].style.animationPlayState="paused",sections[e].style.animationFillMode="forwards",sections[e].style.animationDirection="normal"}scrollPos=e}));
