const showOrHideSection=(button)=>{let reference=button.id;reference="#"+button.id;console.log(reference);let currentSection=document.querySelector(`.sections:has(${reference}) .section--show-education`)||document.querySelector(`.sections:has( ${reference}) .section--show-work`);let nextSection=document.querySelector(reference+" + "+"section");let classShow=(currentSection.classList.contains("section--show-education")?"section--show-education":"section--show-work");let classHide=(classShow==="section--show-education"?"section--hide-education":"section--hide-work");if(!(currentSection===nextSection)){nextSection.classList.remove(classHide);console.log(classHide);nextSection.classList.add(classShow);currentSection.classList.remove(classShow);currentSection.classList.add(classHide);console.log(classShow)}}
let buttonsWork=document.querySelectorAll("#work .timeline__key-moment-a");let buttonsEducation=document.querySelectorAll("#education .timeline__key-moment-a");addListenerToTLineLink(buttonsWork);addListenerToTLineLink(buttonsEducation);function addListenerToTLineLink(buttons){Array.from(buttons).map(button=>button.addEventListener("click",()=>showOrHideSection(button)))}
function showOrHideDescription(index){let textDescriptions=document.getElementsByClassName("project-container__description");let textDescription=textDescriptions.item(index);textDescription.classList.toggle("display")}
function addListenerToDescriptionButton(buttons){Array.from(buttons).map(button=>{button.addEventListener("click",()=>showOrHideDescription(Array.from(buttons).indexOf(button)))})}
let buttonsDescription=document.getElementsByClassName("project-container__find-out-more");addListenerToDescriptionButton(buttonsDescription)